import React from "react";

const Pagination=({totalPosts,postsPerPage})=>{
  const pageNumbers=[];
  for(let i=1;i<= totalPosts/postsPerPage;i++){
    pageNumbers.push(i);
  }
  return(
    <nav>
      <ul>
       {pageNumbers.map((page)=>(
         <li>
           <a href="!#">
             {page}
           </a>
         </li>
       ))}
      </ul>
    </nav>
  )
}
export default Pagination;
